package q5

import (
	"testing"
	"github.com/stretchr/testify/assert"
)

func TestCode(t *testing.T) {
	assert.Equal(t, "a", longestPalindrome("a"))
	assert.Equal(t, "bb", longestPalindrome("bb"))
	assert.Equal(t, "bcdcb", longestPalindrome("abcdcb"))
	assert.Equal(t, "ranynar", longestPalindrome(""))
	//a1000 := ""
	//assert.Equal(t, a1000, longestPalindrome(a1000))
	assert.Equal(t, "ccc", longestPalindrome("ccc"))
}

func BenchmarkCode(b *testing.B) {
	for i := 0; i < b.N; i++ {
		longestPalindrome("")
	}
}
